async function beep(count = 1, intervalMS = 100) {
  for (let i = 0; i < count; i++) {
    console.log('!!');
    const snd = new Audio("data:audio/wav;base64,");  
    await new Promise(resolve => setTimeout(() => resolve(), intervalMS));
    snd.play();
  }
}

const cmds = {
  'en-US': {
    over: 'over',
    remove: 'delete',
    drop: 'drop',
  },
  'zh-CN': {
    over: '结束',
    remove: '删除',
    drop: '重写'
  }
}

const curLang = 'en-US';
// const curLang = 'zh-CN';

function cmdIs(cmd, str) {
  return cmd == str || 
    cmd == str[0].toUpperCase() + str.slice(1) ||
    cmd.match(`${str}$`);
}


export default async function(cbOnSpeech, cbOnOver) {
  window.SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
  window.SpeechGrammarList = window.SpeechGrammarList || window.webkitSpeechGrammarList;
  window.SpeechRecognitionEvent = window.SpeechRecognitionEvent || window.webkitSpeechRecognitionEvent;
  const recognition = new SpeechRecognition();
  recognition.continuous = false;
  recognition.lang = curLang;
  recognition.interimResults = false;
  recognition.maxAlternatives = 1;
  let recoStarted = false;
  const resultList = [];

  console.log('begin recoMain!!');
  const delIdxList = [];

  recognition.onresult = async function (event) {
    console.log('raw event = ', event);

    const cmd = event.results[event.results.length - 1][0].transcript.trim();
    // console.log('refresh? =', refresh);

    if (cmdIs(cmd, cmds[curLang].over)) {
      console.log('over!');
      recognition.stop();
      resultList.length = 0;
      await beep();

      await new Promise(resolve => setTimeout(() => resolve(), 300));

      if (cbOnOver) {
        await cbOnOver();
      }
      await new Promise(resolve => setTimeout(() => resolve(), 300));
      await beep(2);
      recognition.start();
      return;
    } else if (cmdIs(cmd, cmds[curLang].remove)) {
      // const delIdx = event.results.length - 1;
      // delIdxList.push(delIdx);
      // delIdxList.push(delIdx-1);
      resultList.splice(-1, 1);
      console.log('remove!');
      const transcript = resultList.join(', ');
      domTextArea.value = transcript;
      return;
    }

    console.log('original event.results:', event.results);
    // for (let i = 0; i < event.results.length; i++) {
      // if (!delIdxList.includes(i)) {
    resultList.push(event.results[0][0].transcript);
      // }
    // }
    console.log('resultList:', resultList);
    const transcript = resultList.join(', ');
    console.log('transcript:', transcript);
    cbOnSpeech(transcript);
    // domTextArea.value = transcript;
    // resultList.length = 0;
    recognition.stop();
  }

  recognition.onend = function (event) {
    if (recoStarted && resultList.length > 0) {
      console.log('recognition.onend event = ', event);
      recognition.start();
    }
  }

  console.log('start recognition');
  recognition.start();
  recoStarted = true;
}